mapper.update:
update element_library set			
	status = #{status}
where emId = #{emId}

mapper.select:
select a.*,b.tbId from element_library a inner join table_element b on a.emId = b.emId and b.tbId = #{tbId}
and b.scope = #{scope}
where a.status = '有效' union all select a.*,null as tbId from element_library a where a.status = '有效' and a.emId not in (select emId from table_element b where b.tbId = #{tbId} and b.emId is not null)
