mapper.insert:
select sq_emId.nextval from dual
insert into element_library( emId,labelName,fieldName,hiddenFieldName,functionName,status,createdDate,grade,fieldType,fieldDataType,dataContent,length ) values( #{emId}, #{labelName,jdbcType=VARCHAR}, #{fieldName,jdbcType=VARCHAR}, #{hiddenFieldName,jdbcType=VARCHAR}, #{functionName,jdbcType=VARCHAR}, #{status,jdbcType=VARCHAR}, #{createdDate,jdbcType=TIMESTAMP}, #{grade,jdbcType=VARCHAR}, #{fieldType,jdbcType=VARCHAR}, #{fieldDataType,jdbcType=VARCHAR}, #{dataContent,jdbcType=VARCHAR}, #{length,jdbcType=DECIMAL}
)

mapper.update:
update element_library set			
	status = #{status}
where emId = #{emId}

mapper.select:
select a.*,b.tbId from element_library a inner join table_element b on a.emId = b.emId and b.tbId = #{tbId}
and b.scope = #{scope}
where a.status = '有效' union all select a.*,null as tbId from element_library a where a.status = '有效' and a.emId not in (select emId from table_element b where b.tbId = #{tbId})


SELECT *
	FROM  (
	   SELECT a.*, rownum rn
	   FROM (

	      select * from element_library order by emId

	   )a
	   WHERE rownum  <=   #{recordLastNumber}
	)
	WHERE  rn  >=   #{recordNumber}
