mapper.select:

SELECT *
FROM  (
   SELECT a.*, rownum rn
   FROM (

      select * from table_brief	order by tbId 

   )a
   WHERE rownum  <=   #{recordLastNumber}
)
WHERE  rn  >=   #{recordNumber}

mapper.update:
update table_brief set 

		name=#{name,jdbcType=VARCHAR}


		tableName=#{tableName,jdbcType=VARCHAR}


		template=#{template,jdbcType=VARCHAR},


		cols=#{cols,jdbcType=DECIMAL}


		status=#{status,jdbcType=VARCHAR}


	tbId=#{tbId,jdbcType=DECIMAL}

mapper.insert:
{call declare begin
insert into table_element( tbId,emId,newLabelName,newFunctionName,functionBelongTo,rowes,cols,width,scope,seq,newHiddenFieldName, newDataContent,newFieldType,newFieldDataType,newLength,defaultValue,defaultValueFrom,list,createdDate ) values ( #{item.tbId,jdbcType=DECIMAL}, #{item.emId,jdbcType=DECIMAL}, #{item.newLabelName,jdbcType=VARCHAR}, #{item.newFunctionName,jdbcType=VARCHAR}, #{item.functionBelongTo,jdbcType=VARCHAR}, #{item.rowes,jdbcType=DECIMAL}, #{item.cols,jdbcType=DECIMAL}, #{item.width,jdbcType=DECIMAL}, #{item.scope,jdbcType=VARCHAR}, #{item.seq,jdbcType=DECIMAL}, #{item.newHiddenFieldName,jdbcType=VARCHAR}, #{item.newDataContent,jdbcType=VARCHAR}, #{item.newFieldType,jdbcType=VARCHAR}, #{item.newFieldDataType,jdbcType=VARCHAR}, #{item.newLength,jdbcType=DECIMAL}, #{item.defaultValue,jdbcType=VARCHAR}, #{item.defaultValueFrom,jdbcType=VARCHAR}, #{item.list,jdbcType=VARCHAR}, #{item.createdDate,jdbcType=TIMESTAMP} );
end }

mapper.update:
update table_element set 			
	   seq=#{seq}

      tbId=#{tbId}
  and emId = #{emId}

mapper.delete:
delete from table_element 

      tbId=#{tbId}
  and emId = #{emId}

mapper.select:
select a.*,b.labelName,b.fieldName from table_element a
 inner join element_library b on a.emId = b.emId 
 where a.tbId = #{tbId}

   and a.scope = #{scope}

 order by a.seq

select a.* from table_element a
 where a.tbId = #{tbId} 
   and a.emId = #{emId}

mapper.update:
		update table_element set 			
			   list='有效'

		      tbId=#{tbId}
		  and emId in

              ${item}

update table_element set 			
	   list='无效'

      tbId=#{tbId}

mapper.select:
select a.* from table_element a
 inner join workflow b on b.tbId = a.tbId
 inner join workflow_table_element c on b.wfId = c.wfId and a.emId=c.emId
 where a.tbId = #{tbId}
   and b.wfId = #{wfId}
   and c.nodeId = #{nodeId}
 order by a.seq

mapper.insert:
delete from workflow_table_element where wfId = #{wfId} and nodeId = #{nodeId};
insert into workflow_table_element(
	wfId,nodeId,emId
)
values

(
	#{wfId},
	#{nodeId},
	#{item}
)

mapper.select:
select count(a.wfId) from table_summary a
inner join workflow b on a.wfId = b.wfId
where b.tbId = #{tbId}
