mapper.insert:

	select sq_wfId.nextval from dual

insert into workflow(
	wfId,wfName,status,createdDate
)
values(
	#{wfId},
	#{wfName,jdbcType=VARCHAR},			
	#{status,jdbcType=VARCHAR},
	#{createdDate,jdbcType=TIMESTAMP}
)

mapper.update:
update workflow set 

		wfName = #{wfName,jdbcType=VARCHAR} 


		tbId = #{tbId,jdbcType=DECIMAL} 


		status = #{status,jdbcType=VARCHAR} 

	where wfId = #{wfId,jdbcType=DECIMAL}

update workflow set 			
	tbId = null 
	where wfId = #{wfId}

mapper.select:

SELECT *
FROM  (
   SELECT a.*, rownum rn
   FROM (

      select * from workflow order by wfId

   )a
   WHERE rownum  <=   #{recordLastNumber}
)
WHERE  rn  >=   #{recordNumber}
