mapper.update:
update workflow_flow set 
	finishedDate=#{finishedDate},
	actionName=#{actionName},
	status=#{status}


		and flowId=#{flowId}


		and bizId=#{bizId}
		and wfId = #{wfId}

mapper.select:
		select distinct wfs.dispatchUserId as userId,wfs.dispatchUserName as userName,wfs.unitId,wfs.unitName 
		  from workflow_step wfs 
         where flowId = #{flowId}

select * from workflow_flow 
 where flowId in (
	select flowParentId 
	  from workflow_flow 
	 where bizId = #{bizId} 
	   and wfId = #{wfId} 
	   and finishedDate is null
)

select * from workflow_flow 
 where flowId in (
	select min(flowId) 
	  from workflow_flow 
	 where bizId = #{bizId} 
	   and wfId = #{wfId}
)
