mapper.update:
update table_summary set 			
	modifiedDate=#{modifiedDate},
	action=#{action}

	,curUserId=#{curUserId}


	,curUserName=#{curUserName}


	bizId=#{bizId}
and wfId=#{wfId}

update table_summary set 
	modifiedDate=#{modifiedDate},			
	finishedDate=#{finishedDate},
	action=#{action}

	bizId=#{bizId}
and wfId=#{wfId}

mapper.select:
select c.name as tableName,1 as readOnly,a.*,b.labelName,b.fieldName from table_element a
 left join element_library b on a.emId = b.emId		 
 inner join table_brief c on c.tbId = a.tbId 
 where a.tbId = #{tbId}

   and a.scope = #{scope}

 order by a.seq

select c.name as tableName,(case when d.id is not null then 0 else 1 end) as readOnly, d.required,a.*,b.labelName,b.fieldName from table_element a left join element_library b on a.emId = b.emId left join workflow_table_element d on d.id = a.id and d.wfId = #{wfId} and d.nodeName = #{nodeName} inner join table_brief c on c.tbId = a.tbId where a.tbId = #{tbId}
and a.scope = #{scope}
order by a.seq

		select a.tbid,a.name,b.tablename,a.template,a.cols,a.status,a.createddate,a.templatename,b.wfid,b.defId 
		  from table_brief a
          inner join table_user_defination b on a.tbid = b.tbid and b.wfid = #{wfId}
		 where a.tbId = #{tbId}

		select a.tbid,a.name,b.tablename,a.template,a.cols,a.status,a.createddate,a.templatename,b.wfid,b.defId 
		  from table_brief a
          inner join table_user_defination b on a.tbid = b.tbid and b.defId = #{defId}

		select a.*
		  from table_user_defination a
         where a.type = #{type}	
            and a.tbId = #{tbId}	

         	and a.userId = #{userId}

		select a.*
		  from table_user_defination a
         where a.defId = #{defId}

mapper.update:
update table_summary set 			
	curUserId= #{curUserId,jdbcType=DECIMAL},
	curUserName= #{curUserName,jdbcType=VARCHAR}

	wfId =#{wfId,jdbcType=DECIMAL}
and	bizId = #{bizId,jdbcType=DECIMAL}
and	curUserId = #{oldCurUserId,jdbcType=DECIMAL}
